<?phpnamespace Core;use App\Models\Product;use samdark\sitemap\Sitemap;use samdark\sitemap\Index;class MapBuilder{    public function __construct()    {        $sitemap = new Sitemap(dirname(__DIR__) . '/sitemap.xml');        $sitemap->addItem(base_url());        $sitemap->write();        $sitemapFileUrls = $sitemap->getSitemapUrls(base_url());        $staticSitemap = new Sitemap(dirname(__DIR__) . '/sitemap_static.xml');//        $staticSitemap->addItem(base_url("tr/hakkimizda"));        $staticSitemap->write();        $staticSitemapUrls = $staticSitemap->getSitemapUrls(base_url());        $index = new Index(dirname(__DIR__) . '/sitemap_index.xml');        foreach ($sitemapFileUrls as $sitemapUrl) {            $index->addSitemap($sitemapUrl);        }        foreach ($staticSitemapUrls as $sitemapUrl) {            $index->addSitemap($sitemapUrl);        }        $index->write();    }}?>